import express from 'express';

const app = express();
const PORT = process.env.PORT || 3000;

app.get('/', (req, res) => {
  res.json({ 
    message: 'Simple Bun Dev Server! ðŸ”¥',
    timestamp: new Date().toISOString(),
    runtime: 'bun with hot reload',
    version: '1.0.0'
  });
});

app.get('/health', (req, res) => {
  res.json({ 
    status: 'healthy',
    uptime: process.uptime(),
    pid: process.pid
  });
});

app.listen(PORT, '0.0.0.0', () => {
  console.log(`ðŸ”¥ Simple Bun server running on http://0.0.0.0:${PORT}`);
});
