import express from 'express';

const app = express();
const PORT = process.env.PORT || 3000;

app.get('/', (req, res) => {
  res.json({ 
    message: 'ðŸš€ UPDATED! Hot reload is working! âš¡',
    timestamp: new Date().toISOString(),
    runtime: 'bun with live updates',
    version: '2.0.0',
    updated: true
  });
});

app.get('/health', (req, res) => {
  res.json({ 
    status: 'healthy',
    uptime: process.uptime(),
    pid: process.pid,
    updated: 'YES! File update worked!'
  });
});

app.get('/new-endpoint', (req, res) => {
  res.json({
    message: 'This is a brand new endpoint added via file update!',
    timestamp: new Date().toISOString(),
    cool: true
  });
});

app.listen(PORT, '0.0.0.0', () => {
  console.log(`ðŸ”¥âš¡ UPDATED Bun server running on http://0.0.0.0:${PORT}`);
});
